 1→import { Lane } from './Lane';
 2→import { Vehicle } from './Vehicle';
 3→
 4→export class Road {
 5→  lanes: Lane[];
 6→  length: number;
 7→
 8→  constructor(length: number, laneCount: number) {
 9→    this.length = length;
10→    this.lanes = [];
11→    for (let i = 0; i < laneCount; i++) {
12→      this.lanes.push(new Lane(i));
13→    }
14→  }
15→
16→  getLeader(vehicle: Vehicle): Vehicle | null {
17→    return this.lanes[vehicle.laneIndex].getLeaderOf(vehicle);
18→  }
19→
20→  getFollower(vehicle: Vehicle): Vehicle | null {
21→    return this.lanes[vehicle.laneIndex].getFollowerOf(vehicle);
22→  }
23→
24→  getNeighbors(
25→    vehicle: Vehicle,
26→    targetLane: number,
27→  ): { leader: Vehicle | null; follower: Vehicle | null } {
28→    if (targetLane < 0 || targetLane >= this.lanes.length) {
29→      return { leader: null, follower: null };
30→    }
31→    const lane = this.lanes[targetLane];
32→    let leader: Vehicle | null = null;
33→    let follower: Vehicle | null = null;
34→
35→    for (let i = 0; i < lane.vehicles.length; i++) {
36→      const v = lane.vehicles[i];
37→      if (v.x >= vehicle.x) {
38→        leader = v;
39→      } else {
40→        follower = v;
41→        break;
42→      }
43→    }
44→
45→    return { leader, follower };
46→  }
47→
48→  getAllVehicles(): Vehicle[] {
49→    const all: Vehicle[] = [];
50→    for (const lane of this.lanes) {
51→      for (const v of lane.vehicles) {
52→        all.push(v);
53→      }
54→    }
55→    return all;
56→  }
57→
58→  changeLane(vehicle: Vehicle, targetLane: number): void {
59→    if (targetLane < 0 || targetLane >= this.lanes.length) return;
60→    this.lanes[vehicle.laneIndex].removeVehicle(vehicle);
61→    vehicle.laneIndex = targetLane;
62→    this.lanes[targetLane].addVehicle(vehicle);
63→  }
64→
65→  resortAllLanes(): void {
66→    for (const lane of this.lanes) {
67→      lane.resort();
68→    }
69→  }
70→}
71→

 1→import { Vehicle } from './Vehicle';
 2→
 3→export class Lane {
 4→  index: number;
 5→  vehicles: Vehicle[];
 6→
 7→  constructor(index: number) {
 8→    this.index = index;
 9→    this.vehicles = [];
10→  }
11→
12→  addVehicle(vehicle: Vehicle): void {
13→    // Insert in correct sorted position (descending by x)
14→    let insertIndex = 0;
15→    while (insertIndex < this.vehicles.length && this.vehicles[insertIndex].x > vehicle.x) {
16→      insertIndex++;
17→    }
18→    this.vehicles.splice(insertIndex, 0, vehicle);
19→  }
20→
21→  removeVehicle(vehicle: Vehicle): void {
22→    const idx = this.vehicles.indexOf(vehicle);
23→    if (idx !== -1) {
24→      this.vehicles.splice(idx, 1);
25→    }
26→  }
27→
28→  getLeaderOf(vehicle: Vehicle): Vehicle | null {
29→    const idx = this.vehicles.indexOf(vehicle);
30→    if (idx <= 0) return null;
31→    return this.vehicles[idx - 1];
32→  }
33→
34→  getFollowerOf(vehicle: Vehicle): Vehicle | null {
35→    const idx = this.vehicles.indexOf(vehicle);
36→    if (idx === -1 || idx >= this.vehicles.length - 1) return null;
37→    return this.vehicles[idx + 1];
38→  }
39→
40→  getVehicleAhead(x: number): Vehicle | null {
41→    // Find first vehicle with position > x (sorted descending, so search from end)
42→    for (let i = this.vehicles.length - 1; i >= 0; i--) {
43→      if (this.vehicles[i].x > x) {
44→        return this.vehicles[i];
45→      }
46→    }
47→    return null;
48→  }
49→
50→  resort(): void {
51→    // Insertion sort — efficient for nearly-sorted arrays
52→    for (let i = 1; i < this.vehicles.length; i++) {
53→      const current = this.vehicles[i];
54→      let j = i - 1;
55→      while (j >= 0 && this.vehicles[j].x < current.x) {
56→        this.vehicles[j + 1] = this.vehicles[j];
57→        j--;
58→      }
59→      this.vehicles[j + 1] = current;
60→    }
61→  }
62→}
