 1→import type { SimulationParams } from '../types';
 2→import { randomNormal } from '../utils/math';
 3→import { Road } from './Road';
 4→import { Vehicle } from './Vehicle';
 5→
 6→export class VehicleFactory {
 7→  private nextId: number = 0;
 8→  private spawnAccumulator: number = 0;
 9→
10→  trySpawn(dt: number, road: Road, params: SimulationParams): void {
11→    this.spawnAccumulator += params.spawnRate * dt;
12→
13→    while (this.spawnAccumulator >= 1) {
14→      this.spawnAccumulator -= 1;
15→      this.attemptSpawn(road, params);
16→    }
17→  }
18→
19→  despawn(road: Road): void {
20→    for (const lane of road.lanes) {
21→      const toRemove: Vehicle[] = [];
22→      for (const v of lane.vehicles) {
23→        if (v.x > road.length) {
24→          toRemove.push(v);
25→        }
26→      }
27→      for (const v of toRemove) {
28→        lane.removeVehicle(v);
29→      }
30→    }
31→  }
32→
33→  private attemptSpawn(road: Road, params: SimulationParams): void {
34→    const laneIndex = this.pickLane(road.lanes.length);
35→    const lane = road.lanes[laneIndex];
36→
37→    // Check gap at entrance: last vehicle in sorted order (closest to x=0) must be far enough
38→    const minGap = params.minimumGap + params.vehicleLength;
39→    if (lane.vehicles.length > 0) {
40→      const last = lane.vehicles[lane.vehicles.length - 1];
41→      if (last.x < minGap) {
42→        return; // entrance blocked
43→      }
44→    }
45→
46→    const desiredSpeed = Math.max(1, randomNormal(params.desiredSpeed, 0.1 * params.desiredSpeed));
47→    const vehicle = new Vehicle(
48→      this.nextId++,
49→      0,
50→      desiredSpeed,
51→      laneIndex,
52→      desiredSpeed,
53→      params.vehicleLength,
54→    );
55→    lane.addVehicle(vehicle);
56→  }
57→
58→  /** Pick a lane with bias toward rightmost (higher index = more weight). */
59→  private pickLane(laneCount: number): number {
60→    // Weights: lane 0 gets weight 1, lane 1 gets weight 2, etc.
61→    const totalWeight = (laneCount * (laneCount + 1)) / 2;
62→    let r = Math.random() * totalWeight;
63→    for (let i = laneCount - 1; i >= 0; i--) {
64→      r -= i + 1;
65→      if (r <= 0) return i;
66→    }
67→    return 0;
68→  }
69→}
