 1→import type { SimulationParams } from '../types';
 2→
 3→const LANE_WIDTH = 3.7; // meters (standard lane width)
 4→const SHOULDER_WIDTH = 1.5; // meters
 5→const DASH_LENGTH = 3.0; // meters
 6→const DASH_GAP = 9.0; // meters
 7→const MARKING_WIDTH = 0.15; // meters
 8→
 9→const ROAD_COLOR = '#374151'; // dark gray
10→const SHOULDER_COLOR = '#4b5563'; // lighter gray
11→const MARKING_COLOR = '#ffffff';
12→
13→export class RoadRenderer {
14→  static draw(
15→    ctx: CanvasRenderingContext2D,
16→    roadLength: number,
17→    laneCount: number,
18→    laneWidth: number = LANE_WIDTH,
19→  ): void {
20→    const roadHeight = laneCount * laneWidth;
21→
22→    // 1. Road shoulders (slightly lighter gray)
23→    ctx.fillStyle = SHOULDER_COLOR;
24→    ctx.fillRect(0, -SHOULDER_WIDTH, roadLength, roadHeight + 2 * SHOULDER_WIDTH);
25→
26→    // 2. Dark gray road surface rectangle
27→    ctx.fillStyle = ROAD_COLOR;
28→    ctx.fillRect(0, 0, roadLength, roadHeight);
29→
30→    // 3. Solid white edge lines top and bottom
31→    ctx.strokeStyle = MARKING_COLOR;
32→    ctx.lineWidth = MARKING_WIDTH;
33→    ctx.beginPath();
34→    ctx.moveTo(0, 0);
35→    ctx.lineTo(roadLength, 0);
36→    ctx.moveTo(0, roadHeight);
37→    ctx.lineTo(roadLength, roadHeight);
38→    ctx.stroke();
39→
40→    // 4. White dashed lane markings between lanes
41→    ctx.setLineDash([DASH_LENGTH, DASH_GAP]);
42→    for (let i = 1; i < laneCount; i++) {
43→      const y = i * laneWidth;
44→      ctx.beginPath();
45→      ctx.moveTo(0, y);
46→      ctx.lineTo(roadLength, y);
47→      ctx.stroke();
48→    }
49→    ctx.setLineDash([]);
50→  }
51→}
