 1→import type { VehicleState } from '../types';
 2→import { speedToColor } from '../utils/colors';
 3→
 4→const VEHICLE_WIDTH = 2.0; // meters
 5→const CORNER_RADIUS = 0.5; // meters
 6→
 7→export class VehicleRenderer {
 8→  static draw(
 9→    ctx: CanvasRenderingContext2D,
10→    vehicles: VehicleState[],
11→    laneWidth: number,
12→    maxSpeed: number,
13→  ): void {
14→    for (const vehicle of vehicles) {
15→      const x = vehicle.x - vehicle.length / 2;
16→      const y = vehicle.laneIndex * laneWidth + (laneWidth - VEHICLE_WIDTH) / 2;
17→      const w = vehicle.length;
18→      const h = VEHICLE_WIDTH;
19→      const r = Math.min(CORNER_RADIUS, w / 2, h / 2);
20→
21→      // Color based on speed
22→      ctx.fillStyle = speedToColor(vehicle.speed, maxSpeed);
23→
24→      // Draw rounded rectangle
25→      ctx.beginPath();
26→      ctx.moveTo(x + r, y);
27→      ctx.lineTo(x + w - r, y);
28→      ctx.arcTo(x + w, y, x + w, y + r, r);
29→      ctx.lineTo(x + w, y + h - r);
30→      ctx.arcTo(x + w, y + h, x + w - r, y + h, r);
31→      ctx.lineTo(x + r, y + h);
32→      ctx.arcTo(x, y + h, x, y + h - r, r);
33→      ctx.lineTo(x, y + r);
34→      ctx.arcTo(x, y, x + r, y, r);
35→      ctx.closePath();
36→      ctx.fill();
37→
38→      // Direction indicator (small triangle at front)
39→      ctx.fillStyle = 'rgba(255, 255, 255, 0.4)';
40→      const triSize = Math.min(0.8, w * 0.2);
41→      const triX = x + w - triSize - 0.2;
42→      const triCenterY = y + h / 2;
43→      ctx.beginPath();
44→      ctx.moveTo(triX + triSize, triCenterY);
45→      ctx.lineTo(triX, triCenterY - triSize / 2);
46→      ctx.lineTo(triX, triCenterY + triSize / 2);
47→      ctx.closePath();
48→      ctx.fill();
49→    }
50→  }
51→}
