 1→export interface SimulationParams {
 2→  // IDM
 3→  desiredSpeed: number;          // v0, m/s
 4→  timeHeadway: number;           // T, seconds
 5→  maxAcceleration: number;       // a, m/s²
 6→  comfortDeceleration: number;   // b, m/s²
 7→  minimumGap: number;            // s0, meters
 8→  vehicleLength: number;         // meters
 9→
10→  // MOBIL
11→  politenessFactor: number;      // p
12→  changingThreshold: number;     // delta_a_th, m/s²
13→  safeDeceleration: number;      // b_safe, m/s²
14→
15→  // Road
16→  roadLengthMeters: number;
17→  laneCount: number;
18→
19→  // Traffic demand
20→  spawnRate: number;             // vehicles per second
21→
22→  // Simulation
23→  dt: number;                    // fixed timestep, seconds
24→  speedMultiplier: number;       // 1x, 2x, 5x, 10x
25→}
26→
27→export interface VehicleState {
28→  id: number;
29→  x: number;
30→  laneIndex: number;
31→  speed: number;
32→  acceleration: number;
33→  length: number;
34→  desiredSpeed: number;
35→}
