37→export interface IncidentConfig {
38→  id: number;
39→  positionX: number;
40→  lanesBlocked: number[];
41→  severity: number;              // 0-1
42→  startTime: number;
43→  duration: number;              // seconds, -1 for manual removal
44→  rubberneckingFactor: number;   // 0.5-1.0
45→}
46→
47→export interface SimulationState {
48→  vehicles: VehicleState[];
49→  simulationTime: number;
50→}
51→
52→export interface RoadSegmentData {
53→  segmentIndex: number;
54→  density: number;               // vehicles per km
55→  flow: number;                  // vehicles per hour
56→  averageSpeed: number;          // m/s
57→}
