  1→import { describe, it, expect } from 'vitest';
  2→import { Road } from '../Road';
  3→import { Vehicle } from '../Vehicle';
  4→
  5→function makeVehicle(id: number, x: number, laneIndex: number): Vehicle {
  6→  return new Vehicle(id, x, 30, laneIndex, 33.3);
  7→}
  8→
  9→describe('Road', () => {
 10→  it('creates the correct number of lanes', () => {
 11→    const road = new Road(1000, 3);
 12→    expect(road.lanes).toHaveLength(3);
 13→    expect(road.length).toBe(1000);
 14→  });
 15→
 16→  it('getLeader delegates to Lane', () => {
 17→    const road = new Road(1000, 2);
 18→    const v1 = makeVehicle(1, 100, 0);
 19→    const v2 = makeVehicle(2, 50, 0);
 20→    road.lanes[0].addVehicle(v1);
 21→    road.lanes[0].addVehicle(v2);
 22→
 23→    expect(road.getLeader(v2)).toBe(v1);
 24→    expect(road.getLeader(v1)).toBeNull();
 25→  });
 26→
 27→  it('getFollower delegates to Lane', () => {
 28→    const road = new Road(1000, 2);
 29→    const v1 = makeVehicle(1, 100, 0);
 30→    const v2 = makeVehicle(2, 50, 0);
 31→    road.lanes[0].addVehicle(v1);
 32→    road.lanes[0].addVehicle(v2);
 33→
 34→    expect(road.getFollower(v1)).toBe(v2);
 35→    expect(road.getFollower(v2)).toBeNull();
 36→  });
 37→
 38→  it('getNeighbors finds correct leader/follower in adjacent lane', () => {
 39→    const road = new Road(1000, 3);
 40→    const subject = makeVehicle(0, 50, 1);
 41→    road.lanes[1].addVehicle(subject);
 42→
 43→    const ahead = makeVehicle(1, 80, 0);
 44→    const behind = makeVehicle(2, 20, 0);
 45→    road.lanes[0].addVehicle(ahead);
 46→    road.lanes[0].addVehicle(behind);
 47→
 48→    const neighbors = road.getNeighbors(subject, 0);
 49→    expect(neighbors.leader).toBe(ahead);
 50→    expect(neighbors.follower).toBe(behind);
 51→  });
 52→
 53→  it('getNeighbors returns nulls for out-of-range lane', () => {
 54→    const road = new Road(1000, 2);
 55→    const v = makeVehicle(0, 50, 0);
 56→    road.lanes[0].addVehicle(v);
 57→
 58→    const neighbors = road.getNeighbors(v, -1);
 59→    expect(neighbors.leader).toBeNull();
 60→    expect(neighbors.follower).toBeNull();
 61→  });
 62→
 63→  it('getNeighbors handles no leader or no follower', () => {
 64→    const road = new Road(1000, 2);
 65→    const subject = makeVehicle(0, 50, 0);
 66→    road.lanes[0].addVehicle(subject);
 67→
 68→    // Only a vehicle behind in lane 1
 69→    const behind = makeVehicle(1, 20, 1);
 70→    road.lanes[1].addVehicle(behind);
 71→
 72→    const neighbors = road.getNeighbors(subject, 1);
 73→    expect(neighbors.leader).toBeNull();
 74→    expect(neighbors.follower).toBe(behind);
 75→  });
 76→
 77→  it('changeLane moves vehicle between lanes', () => {
 78→    const road = new Road(1000, 3);
 79→    const v = makeVehicle(0, 50, 0);
 80→    road.lanes[0].addVehicle(v);
 81→
 82→    expect(road.lanes[0].vehicles).toContain(v);
 83→    road.changeLane(v, 1);
 84→    expect(road.lanes[0].vehicles).not.toContain(v);
 85→    expect(road.lanes[1].vehicles).toContain(v);
 86→    expect(v.laneIndex).toBe(1);
 87→  });
 88→
 89→  it('changeLane does nothing for out-of-range target', () => {
 90→    const road = new Road(1000, 2);
 91→    const v = makeVehicle(0, 50, 0);
 92→    road.lanes[0].addVehicle(v);
 93→
 94→    road.changeLane(v, 5);
 95→    expect(road.lanes[0].vehicles).toContain(v);
 96→    expect(v.laneIndex).toBe(0);
 97→  });
 98→
 99→  it('getAllVehicles returns all vehicles across all lanes', () => {
100→    const road = new Road(1000, 3);
101→    const v1 = makeVehicle(1, 100, 0);
102→    const v2 = makeVehicle(2, 200, 1);
103→    const v3 = makeVehicle(3, 300, 2);
104→    road.lanes[0].addVehicle(v1);
105→    road.lanes[1].addVehicle(v2);
106→    road.lanes[2].addVehicle(v3);
107→
108→    const all = road.getAllVehicles();
109→    expect(all).toHaveLength(3);
110→    expect(all).toContain(v1);
111→    expect(all).toContain(v2);
112→    expect(all).toContain(v3);
113→  });
114→
115→  it('resortAllLanes resorts all lanes', () => {
116→    const road = new Road(1000, 2);
117→    const v1 = makeVehicle(1, 100, 0);
118→    const v2 = makeVehicle(2, 50, 0);
119→    road.lanes[0].addVehicle(v1);
120→    road.lanes[0].addVehicle(v2);
121→
122→    // Manually mess up the order
123→    v2.x = 200;
124→    road.resortAllLanes();
125→
126→    expect(road.lanes[0].vehicles[0]).toBe(v2);
127→    expect(road.lanes[0].vehicles[1]).toBe(v1);
128→  });
129→});
