  1→import { describe, it, expect } from 'vitest';
  2→import { Lane } from '../Lane';
  3→import { Vehicle } from '../Vehicle';
  4→
  5→function makeVehicle(id: number, x: number, laneIndex = 0): Vehicle {
  6→  return new Vehicle(id, x, 20, laneIndex, 33);
  7→}
  8→
  9→describe('Lane', () => {
 10→  describe('sort invariant', () => {
 11→    it('maintains descending x order when adding vehicles', () => {
 12→      const lane = new Lane(0);
 13→      lane.addVehicle(makeVehicle(1, 100));
 14→      lane.addVehicle(makeVehicle(2, 300));
 15→      lane.addVehicle(makeVehicle(3, 200));
 16→      lane.addVehicle(makeVehicle(4, 50));
 17→
 18→      expect(lane.vehicles.map(v => v.x)).toEqual([300, 200, 100, 50]);
 19→    });
 20→
 21→    it('handles adding vehicles with the same position', () => {
 22→      const lane = new Lane(0);
 23→      lane.addVehicle(makeVehicle(1, 100));
 24→      lane.addVehicle(makeVehicle(2, 100));
 25→
 26→      expect(lane.vehicles.length).toBe(2);
 27→    });
 28→  });
 29→
 30→  describe('getLeaderOf', () => {
 31→    it('returns the vehicle immediately ahead', () => {
 32→      const lane = new Lane(0);
 33→      const rear = makeVehicle(1, 100);
 34→      const front = makeVehicle(2, 200);
 35→      lane.addVehicle(rear);
 36→      lane.addVehicle(front);
 37→
 38→      expect(lane.getLeaderOf(rear)).toBe(front);
 39→    });
 40→
 41→    it('returns null for the frontmost vehicle', () => {
 42→      const lane = new Lane(0);
 43→      const front = makeVehicle(1, 200);
 44→      const rear = makeVehicle(2, 100);
 45→      lane.addVehicle(front);
 46→      lane.addVehicle(rear);
 47→
 48→      expect(lane.getLeaderOf(front)).toBeNull();
 49→    });
 50→  });
 51→
 52→  describe('getFollowerOf', () => {
 53→    it('returns the vehicle immediately behind', () => {
 54→      const lane = new Lane(0);
 55→      const rear = makeVehicle(1, 100);
 56→      const front = makeVehicle(2, 200);
 57→      lane.addVehicle(rear);
 58→      lane.addVehicle(front);
 59→
 60→      expect(lane.getFollowerOf(front)).toBe(rear);
 61→    });
 62→
 63→    it('returns null for the rearmost vehicle', () => {
 64→      const lane = new Lane(0);
 65→      const rear = makeVehicle(1, 100);
 66→      const front = makeVehicle(2, 200);
 67→      lane.addVehicle(rear);
 68→      lane.addVehicle(front);
 69→
 70→      expect(lane.getFollowerOf(rear)).toBeNull();
 71→    });
 72→  });
 73→
 74→  describe('removeVehicle', () => {
 75→    it('removes the vehicle and maintains sort order', () => {
 76→      const lane = new Lane(0);
 77→      const v1 = makeVehicle(1, 100);
 78→      const v2 = makeVehicle(2, 200);
 79→      const v3 = makeVehicle(3, 300);
 80→      lane.addVehicle(v1);
 81→      lane.addVehicle(v2);
 82→      lane.addVehicle(v3);
 83→
 84→      lane.removeVehicle(v2);
 85→
 86→      expect(lane.vehicles.length).toBe(2);
 87→      expect(lane.vehicles.map(v => v.x)).toEqual([300, 100]);
 88→    });
 89→
 90→    it('does nothing when removing a vehicle not in the lane', () => {
 91→      const lane = new Lane(0);
 92→      const v1 = makeVehicle(1, 100);
 93→      const v2 = makeVehicle(2, 200);
 94→      lane.addVehicle(v1);
 95→
 96→      lane.removeVehicle(v2);
 97→
 98→      expect(lane.vehicles.length).toBe(1);
 99→    });
100→  });
101→
102→  describe('resort', () => {
103→    it('fixes order after vehicle positions are modified', () => {
104→      const lane = new Lane(0);
105→      const v1 = makeVehicle(1, 100);
106→      const v2 = makeVehicle(2, 200);
107→      const v3 = makeVehicle(3, 300);
108→      lane.addVehicle(v1);
109→      lane.addVehicle(v2);
110→      lane.addVehicle(v3);
111→
112→      // Simulate position updates that break ordering
113→      v1.x = 350; // was behind, now in front
114→      v3.x = 50;  // was in front, now behind
115→
116→      lane.resort();
117→
118→      expect(lane.vehicles.map(v => v.x)).toEqual([350, 200, 50]);
119→      expect(lane.vehicles[0]).toBe(v1);
120→      expect(lane.vehicles[2]).toBe(v3);
121→    });
122→  });
123→
124→  describe('getVehicleAhead', () => {
125→    it('returns the closest vehicle ahead of a given position', () => {
126→      const lane = new Lane(0);
127→      lane.addVehicle(makeVehicle(1, 100));
128→      lane.addVehicle(makeVehicle(2, 200));
129→      lane.addVehicle(makeVehicle(3, 300));
130→
131→      const ahead = lane.getVehicleAhead(150);
132→      expect(ahead).not.toBeNull();
133→      expect(ahead!.x).toBe(200);
134→    });
