 1→import type { SimulationParams } from '../../types';
 2→import { Vehicle } from '../Vehicle';
 3→
 4→/**
 5→ * Compute IDM (Intelligent Driver Model) acceleration.
 6→ *
 7→ * @param speed        current vehicle speed (m/s)
 8→ * @param desiredSpeed v0 for this vehicle (m/s)
 9→ * @param gap          bumper-to-bumper distance to leader (m), Infinity if no leader
10→ * @param leaderSpeed  leader's speed (m/s)
11→ * @param params       global simulation params (a, b, T, s0)
12→ * @param speedFactor  rubbernecking reduction factor (1.0 = normal)
13→ */
14→export function computeIDMAcceleration(
15→  speed: number,
16→  desiredSpeed: number,
17→  gap: number,
18→  leaderSpeed: number,
19→  params: SimulationParams,
20→  speedFactor: number = 1.0,
21→): number {
22→  const v = speed;
23→  const v0Eff = desiredSpeed * speedFactor;
24→  const a = params.maxAcceleration;
